script:
frappe.ui.form.on('Document', {
    refresh: function(frm) {
        // Проверка что есть child table files
        if (!frm.fields_dict.files || !frm.fields_dict.files.grid) {
            return;
        }
        
        let grid = frm.fields_dict.files.grid;
        
        // Обработчик кликов на ячейки file_url
        grid.wrapper.off('click', '[data-fieldname="file_url"]');  // Убрать старые обработчики
        
        grid.wrapper.on('click', '[data-fieldname="file_url"]', function(e) {
            e.stopPropagation();  // Останавливаем редактирование ячейки
            e.preventDefault();   // Отменяем действие по умолчанию
            
            let $cell = $(this);
            let row_name = $cell.closest('.grid-row').data('name');
            let row = grid.grid_rows_by_docname[row_name];
            
            if (row && row.doc && row.doc.file_url) {
                // Открываем ссылку в новой вкладке
                window.open(row.doc.file_url, '_blank', 'noopener,noreferrer');
                console.log('Открыта ссылка:', row.doc.file_url);
            }
        });
        
        // Стилизуем как ссылки
        grid.wrapper.find('[data-fieldname="file_url"]').each(function() {
            let $cell = $(this);
            let value = $cell.text().trim();
            
            if (value && (value.startsWith('http://') || value.startsWith('https://'))) {
                $cell.css({
                    'color': 'var(--primary)',
                    'text-decoration': 'underline',
                    'cursor': 'pointer',
                    'user-select': 'none'  // Отключить выделение текста
                });
            }
        });
        
        console.log('Client Script: file_url обработчики установлены');
    },
    
    files_on_form_rendered: function(frm) {
        // Обновить стили при каждом рендере grid
        setTimeout(function() {
            if (frm.fields_dict.files && frm.fields_dict.files.grid) {
                frm.fields_dict.files.grid.wrapper.find('[data-fieldname="file_url"]').each(function() {
                    let $cell = $(this);
                    let value = $cell.text().trim();
                    
                    if (value && (value.startsWith('http://') || value.startsWith('https://'))) {
                        $cell.css({
                            'color': 'var(--primary)',
                            'text-decoration': 'underline',
                            'cursor': 'pointer',
                            'user-select': 'none'
                        });
                    }
                });
            }
        }, 100);
    }
});

frappe.ui.form.on('NextCloud Sync Settings', {
    test_connection: function(frm) {
        frappe.call({
            method: 'company_documents.nextcloud_sync.test_nextcloud_connection',
            callback: function(r) {
                if (r.message) {
                    if (r.message.success) {
                        frappe.msgprint(r.message.message, 'Success');
                        frm.set_value('last_test_result', r.message.message);
                    } else {
                        frappe.msgprint(r.message.message, 'Error');
                        frm.set_value('last_test_result', r.message.message);
                    }
                    frm.save();
                }
            }
        });
    }
});

frappe.ui.form.on("Document", {
  refresh: function(frm) {
    setup_level_filters(frm);
  },

  level_1: function(frm) {
    frm.set_value("level_2", "");
    frm.set_value("level_3", "");
    frm.set_value("level_4", "");
    frm.set_value("level_5", "");
    setup_level_filters(frm);
  },

  level_2: function(frm) {
    frm.set_value("level_3", "");
    frm.set_value("level_4", "");
    frm.set_value("level_5", "");
    setup_level_filters(frm);
  },

  level_3: function(frm) {
    frm.set_value("level_4", "");
    frm.set_value("level_5", "");
    setup_level_filters(frm);
  },

  level_4: function(frm) {
    frm.set_value("level_5", "");
    setup_level_filters(frm);
  }
});

function setup_level_filters(frm) {
  frm.set_query("level_1", function() {
    return {
      filters: {
        "parent_folder_structure_template": ["is", "not set"]
      }
    };
  });

  if (frm.doc.level_1) {
    frm.set_query("level_2", function() {
      return {
        filters: {
          "parent_folder_structure_template": frm.doc.level_1
        }
      };
    });
  }

  if (frm.doc.level_2) {
    frm.set_query("level_3", function() {
      return {
        filters: {
          "parent_folder_structure_template": frm.doc.level_2
        }
      };
    });
  }

  if (frm.doc.level_3) {
    frm.set_query("level_4", function() {
      return {
        filters: {
          "parent_folder_structure_template": frm.doc.level_3
        }
      };
    });
  }

  if (frm.doc.level_4) {
    frm.set_query("level_5", function() {
      return {
        filters: {
          "parent_folder_structure_template": frm.doc.level_4
        }
      };
    });
  }
}

frappe.ui.form.on("Document", {
    after_save: function(frm) {
        if (!frm.doc.files || frm.doc.files.length === 0) {
            return;
        }
        
        frappe.call({
            method: "company_documents.nextcloud_sync.sync_document_to_nextcloud",
            args: {
                docname: frm.doc.name
            },
            callback: function(r) {
                frm.reload_doc();
            }
        });
    }
});

frappe.ui.form.on('Document', {
    setup: function(frm) {
        frm.set_query('task', function() {
            if (frm.doc.project) {
                return {
                    filters: { 'project': frm.doc.project }
                };
            } else {
                return {};
            }
        });
    },

    project: function(frm) {
        frm.set_value('task', '');
        frm.refresh_field('task');
    }
});

frappe.ui.form.on('Document', {
    category: function(frm) {
        let subtypes = {
            'Progettazione': [
                'preliminare',
                'definitivo',
                'esecutivo',
                'stato di fatto'
            ],
            'Realizzazione': [
                'preventivo',
                'ordine',
                'cantiere',
                'collaudo'
            ],
            'Amministrativi': [
                'CILA',
                'Autorizzazione',
                'Incentivazione'
            ]
        };
        
        let options = subtypes[frm.doc.category] || [];
        frm.set_df_property('subtype', 'options', options.join('\n'));
        
        if (frm.doc.subtype && !options.includes(frm.doc.subtype)) {
            frm.set_value('subtype', '');
        }
    }
});

frappe.ui.form.on('Document', {
    onload: function(frm) {
        frm.__auto_save_checked = false;
    },
    refresh: function(frm) {
        attachChangeHook(frm);
    }
});

function attachChangeHook(frm) {
    try {
        if (frm.__mandatory_hooked) return;
        frm.__mandatory_hooked = true;

        $(frm.wrapper).on('change keyup', 'input, textarea, select', function() {
            checkAndAutoSave(frm);
        });

        let grid = frm.fields_dict && frm.fields_dict['files'] && frm.fields_dict['files'].grid;
        if (grid && !frm.__files_grid_hooked) {
            frm.__files_grid_hooked = true;
            grid.wrapper.on('click', '.grid-add-row', function() {
                setTimeout(function(){ checkAndAutoSave(frm); }, 200);
            });
        }
    } catch(e){
        console.error('attachChangeHook error', e);
    }
}

function checkAndAutoSave(frm) {
    if (frm.__auto_saved_mandatory) return;
    if (!frm.is_new()) return;

    let mandatory_fields = [];
    (frm.meta.fields || []).forEach(function(f) {
        let skip_types = ['Section Break','Column Break','HTML','Button','Link','Table','Code','Currency','Duration','Image','Check'];
        if (skip_types.indexOf(f.fieldtype) !== -1) return;

        let is_mand = f.reqd || f.mandatory || f.required;
        if (is_mand && !f.hidden && !f.read_only && (!f.permlevel || f.permlevel === 0)) {
            if (f.fieldtype !== 'Table') mandatory_fields.push(f.fieldname);
        }
    });

    if (!mandatory_fields.length) return;

    for (let i=0; i<mandatory_fields.length; i++){
        let fn = mandatory_fields[i];
        let val = frm.doc[fn];

        if (val === null || val === undefined || (typeof val === 'string' && val.trim() === '') ) {
            return;
        }
    }

    frm.__auto_saved_mandatory = true;
    frm.save().then(function() {
        frappe.show_alert({message: 'Черновик сохранён — теперь можно загружать файлы', indicator: 'green'});
    }).catch(function() {
        frm.__auto_saved_mandatory = false;
    });
}
